mapper.select:
SELECT count(*)over() total, bi.businessid id, bi.name, bi.description,
1 as type, br.categoryguid businessCategoryGuid, null technicalCategoryGuid, bi.tenantid tenantId,
null tableGuid, bi.tenantid
FROM businessinfo bi
JOIN business_relation br ON br.businessid=bi.businessid
WHERE 1=1

    AND bi.name like concat('%',#{name},'%') ESCAPE '/'




            AND
            (
            bi.private_status='PUBLIC' OR (bi.submitter=#{userId} AND bi.submitter_read=true)
            OR
            (SELECT count(*) FROM business_2_group b2g
            JOIN user_group_relation ugr ON ugr.group_id = b2g.group_id AND ugr.user_id=#{userId}
            WHERE b2g.business_id=bi.businessid AND b2g.read=true
            )>0
            )



        AND bi.tenantid=#{tenantId}
        AND
        (
        bi.private_status='PUBLIC' OR (bi.submitter=#{userId} AND bi.submitter_read=true)
        OR
        (SELECT count(*) FROM business_2_group b2g
        JOIN user_group_relation ugr ON ugr.group_id = b2g.group_id AND ugr.user_id=#{userId}
        WHERE b2g.business_id=bi.businessid AND b2g.read=true
        )>0
        )


ORDER BY name
limit #{limit}
offset #{offset}

SELECT count(*)over() total, ti.tableguid, sidti.table_name_zh, ti.tablename, sidti.security, sidti.importance, u.username,
sidti.update_frequency , sidti.category_id categoryGuid, #{tenantId} tenantId, ti.tableguid tableGuid
FROM business2table b2t
JOIN tableinfo ti ON ti.tableguid=b2t.tableguid AND ti.status='ACTIVE'
LEFT JOIN source_info_derive_table_info sidti ON sidti.table_guid=ti.tableguid AND sidti.version = -1
LEFT JOIN users u ON u.userid=sidti.creator
WHERE b2t.businessid=#{businessId}
limit #{limit}
offset #{offset}

SELECT ci.column_guid, ci.column_guid cid, sidci.column_name_zh, sidci.secret, sidci.secret_period, sidci.important, sidci.remark, #{tenantId} tenantId FROM column_info ci LEFT JOIN source_info_derive_column_info sidci ON sidci.column_name_en=ci.column_name WHERE ci.column_guid in
#{columnId}
AND sidci.column_guid in ( SELECT column_guid FROM source_info_derive_table_column_relation sidtcr JOIN source_info_derive_table_info sidti ON sidtcr.table_id=sidti.id AND sidti.table_guid=#{tableId} AND sidti.version=-1 )

select * from ( select c.guid as domainId,c.name as domainName,c.tenantid as tenantId,t.name as tenantName,c.private_status from category c left join tenant t on c.tenantid=t.id where c.level=1 and c.categorytype=1 and c.private_status='PUBLIC'
and c.tenantid=#{tenantId}
union SELECT DISTINCT c.guid as domainId,c.name as domainName,c.tenantid as tenantId,t.name as tenantName,c.private_status from category c left join tenant t on c.tenantid=t.id INNER JOIN category_group_relation as relation on c.guid = relation.category_id WHERE c.level=1 and c.private_status = 'PRIVATE' AND c.categorytype =1
AND c.tenantid=#{tenantId}
AND relation.group_id in
#{item}
union SELECT DISTINCT c.guid as domainId,c.name as domainName,c.tenantid as tenantId,t.name as tenantName,c.private_status from category c left join tenant t on c.tenantid=t.id WHERE c.level=1 and c.private_status = 'PRIVATE' AND c.categorytype =1 and c.creator=#{userId}
AND c.tenantid=#{tenantId}
) temp order by temp.tenantId

select c.guid as domainId,c.name as domainName,c.tenantid as tenantId,t.name as tenantName,c.private_status from category c left join tenant t on c.tenantid=t.id where c.parentcategoryguid=#{guid} and c.private_status='PUBLIC'
and c.tenantid=#{tenantId}
union SELECT DISTINCT c.guid as domainId,c.name as domainName,c.tenantid as tenantId,t.name as tenantName,c.private_status from category c left join tenant t on c.tenantid=t.id INNER JOIN category_group_relation as relation on c.guid = relation.category_id WHERE c.parentcategoryguid=#{guid}
AND c.tenantid=#{tenantId}
AND relation.group_id in
#{item}
union SELECT DISTINCT c.guid as domainId,c.name as domainName,c.tenantid as tenantId,t.name as tenantName,c.private_status from category c left join tenant t on c.tenantid=t.id WHERE c.parentcategoryguid=#{guid} and c.creator=#{userId}
AND c.tenantid=#{tenantId}

SELECT COUNT(tableguid) FROM business2table

    where businessid in

        #{item}

SELECT COUNT(tableguid) FROM business2table

    where businessid in

        #{item}

select bi.businessid
from businessinfo bi
join business_relation br on bi.businessid = br.businessid
where br.categoryguid=#{categoryGuid}

    and bi.tenantid=#{tenantId}

and
(bi.private_status='PUBLIC' or (bi.submitter=#{userId} and bi.submitter_read=true)
or
(select count(*) from business_2_group b2g
join user_group_relation ugr on ugr.group_id = b2g.group_id and ugr.user_id=#{userId}
where b2g.business_id=bi.businessid and b2g.read=true)>0
)

select count(*)over() total,bi.businessid,bi.name,bi.owner,bi.manager,bi.maintainer,bi.businesslastupdate,u.username as businessoperator,'0' as status,bi.tenantid from businessinfo bi join business_relation br on bi.businessid = br.businessid left join users u on bi.businessoperator=u.userid where br.categoryguid=#{categoryGuid}
and bi.tenantid=#{tenantId}
and (bi.private_status='PUBLIC' or (bi.submitter=#{userId} and bi.submitter_read=true) or (select count(*) from business_2_group b2g join user_group_relation ugr on ugr.group_id = b2g.group_id and ugr.user_id=#{userId} where b2g.business_id=bi.businessid and b2g.read=true)>0 ) order by bi.businesslastupdate desc limit #{limit} offset #{offset}
