mapper.select:
select m.id, m.name, m.status, to_timestamp(m.operation_time::text,'YYYY-MM-DD HH24:MI:SS') as "operationTime", ( CASE WHEN m.type = 0 THEN '资源审核信息' WHEN m.type = 1 THEN '用户组信息' WHEN m.type = 2 THEN '数据服务审核' WHEN m.type = 3 THEN '需求审批' END ) "typeCn", count(*)over() total from message_center m left join users u on m.create_user = u.account
m.delete = false
and m.type = #{type}
and m.status = #{status}
and m.name like CONCAT('%', #{search}, '%')
and m.tenantId = #{tenantId}
and u.userid = #{userId}
order by m.create_time desc
offset #{offset}
limit #{limit}

select m.name, m.module, ( CASE WHEN m.type = 0 THEN '资源审核信息' WHEN m.type = 1 THEN '用户组信息' WHEN m.type = 2 THEN '数据服务审核' WHEN m.type = 3 THEN '需求审批' END ) "typeCn", m.status, to_timestamp(m.operation_time::text,'YYYY-MM-DD HH24:MI:SS') as "operationTime", u.username as operator, ( CASE WHEN m.process = 0 THEN '已审批' WHEN m.process = 1 THEN '未审批' WHEN m.process = 2 THEN '已授权' WHEN m.process = 3 THEN '已移除' WHEN m.process = 4 THEN '已处理' WHEN m.process = 5 THEN '已反馈' WHEN m.process = 6 THEN '待处理' END ) "processCn", t.name as "tenantName" from message_center m left join users u on m.create_user = u.account left join tenant t on m.tenantId = t.id where m.delete = false
and m.id = #{id}
and m.tenantId = #{tenantId}
and u.userid = #{userId}

select count(*) as num from message_center m left join users u on m.create_user = u.account where m.delete = false and status = 0
and m.type = #{type}
and m.tenantId = #{tenantId}
and u.userid = #{userId}

